package chapter5;

public class stringswitch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str="two";
		switch(str)
		{
		case "one":
			System.out.println("one");
			break;
		case "two":
			System.out.println("tow");
			break;
		case "three":
			System.out.println("three");
			break;
		default:
			System.out.println("no match");
			break;
		
		}

	}

}
